,duty_board_readcnt
,duty_board_create_dt
,duty_board_update_dt
,duty_updater_id

    ,duty_board_end_dt

,emp_writer_id

,project_id

) VALUES (
#{duty_Board_Id}
,#{duty_Board_Title}
,#{duty_Board_Content}
,0
,sysdate
,sysdate
,#{duty_Updater_Id}

    ,#{duty_Board_End_Dt}

,#{emp_Writer_Id}

,#{project_Id}

)

INSERT INTO duty_attach (
                             attach_id
                           ,attach_path
                           ,attach_name
                           ,attach_type
                           ,attach_create_dt
                           ,duty_board_id
) VALUES (
           duty_attach_id_seq.nextval
         ,#{attach_path}
         ,#{attach_name}
         ,#{attach_type}
         ,sysdate
         ,#{duty_Board_Id}
         )

mapper.select:
select D3.* from( select D2.* from( select rownum as seq, D1.* from( SELECT A.duty_board_id ,A.duty_board_title ,A.duty_board_content ,A.duty_board_readcnt ,A.duty_board_create_dt ,A.duty_board_update_dt ,A.duty_board_end_dt ,A.emp_writer_id ,C.emp_name ,C.emp_id ,C.emp_picture ,B.attachCount ,NVL(D.reply_count,0) as replyCount ,(SELECT COUNT(EMP_ID) FROM EMP WHERE DEPT_ID IN ( SELECT DEPT_ID FROM DUTY_RECEPTION WHERE DUTY_BOARD_ID = A.duty_board_id AND DEPT_ID IS NOT NULL )) dept_Cnt ,(SELECT COUNT(DUTY_BOARD_ID) FROM DUTY_RECEPTION WHERE DUTY_BOARD_ID = A.duty_board_id AND EMP_ID IS NOT NULL ) emp_Cnt FROM duty A LEFT OUTER JOIN ( SELECT duty_board_id, COUNT(*) as attachCount FROM duty_attach GROUP BY duty_board_id ) B ON A.duty_board_id=B.duty_board_id JOIN emp C ON A.emp_writer_id=C.emp_id LEFT OUTER JOIN ( SELECT duty_board_id, COUNT(*) AS reply_count FROM duty_reply GROUP BY duty_board_id ) D ON A.duty_board_id=D.duty_board_id
WHERE 1=1
AND emp_writer_id = #{emp_Writer_Id}
AND #{emp_Writer_Id} IN (SELECT T1.EMP_ID FROM DUTY_ENFORCER T1 WHERE T1.DUTY_BOARD_ID = A.duty_board_id)
ORDER BY A.duty_board_create_dt DESC ) D1 ) D2 where seq > #{firstIndex} ) D3 where rownum <= #{recordCountPerPage}

select D3.* from( select D2.* from( select rownum as seq, D1.* from( SELECT A.duty_board_id ,A.duty_board_title ,A.duty_board_content ,A.duty_board_readcnt ,A.duty_board_create_dt ,A.duty_board_update_dt ,A.duty_board_end_dt ,A.emp_writer_id ,C.emp_name ,C.emp_id ,C.emp_picture ,B.attachCount ,NVL(D.reply_count,0) as replyCount ,(SELECT COUNT(EMP_ID) FROM EMP WHERE DEPT_ID IN ( SELECT DEPT_ID FROM DUTY_RECEPTION WHERE DUTY_BOARD_ID = A.duty_board_id AND DEPT_ID IS NOT NULL )) dept_Cnt ,(SELECT COUNT(DUTY_BOARD_ID) FROM DUTY_RECEPTION WHERE DUTY_BOARD_ID = A.duty_board_id AND EMP_ID IS NOT NULL ) emp_Cnt FROM duty A LEFT OUTER JOIN ( SELECT duty_board_id, COUNT(*) as attachCount FROM duty_attach GROUP BY duty_board_id ) B ON A.duty_board_id=B.duty_board_id JOIN emp C ON A.emp_writer_id=C.emp_id LEFT OUTER JOIN ( SELECT duty_board_id, COUNT(*) AS reply_count FROM duty_reply GROUP BY duty_board_id ) D ON A.duty_board_id=D.duty_board_id
WHERE 1=1 AND ( #{emp_Writer_Id} IN (SELECT T2.EMP_ID FROM DUTY_RECEPTION T2 WHERE T2.DUTY_BOARD_ID = A.duty_board_id AND T2.EMP_ID IS NOT NULL ) OR (SELECT DEPT_ID FROM EMP WHERE EMP_ID = #{emp_Writer_Id}) IN (SELECT T3.DEPT_ID FROM DUTY_RECEPTION T3 WHERE T3.DUTY_BOARD_ID = A.duty_board_id AND T3.DEPT_ID IS NOT NULL) )
ORDER BY A.duty_board_create_dt DESC
) D1 ) D2 where seq > #{firstIndex} ) D3 where rownum <= #{recordCountPerPage}

mapper.update:
UPDATE DUTY_ENFORCER
SET
    read_st = 'Y'
WHERE
    duty_board_id = #{duty_Board_Id}
  AND   emp_id = #{emp_Id}

mapper.select:
select D3.*
from(
        select D2.*
        from(
                select rownum as seq, D1.*
                from(
                        SELECT
                            reply_id
                             ,reply_content
                             ,reply_create_dt
                             ,reply_update_dt
                             ,duty_board_id
                             ,A.EMP_ID
                             ,B.emp_name
                        FROM
                            duty_reply A, emp B
                        WHERE A.EMP_ID=B.emp_id
                          AND duty_board_id=#{duty_Board_Id}
                        ORDER BY reply_create_dt
                    ) D1
            ) D2
        where seq  >  #{firstIndex}
    ) D3
where rownum  <=  #{recordCountPerPage}
